import React, { useState } from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import Button from "@material-ui/core/Button";
let l = [];
function AddResource({ isOpen, onClose }) {
  const [name, setName] = useState("");
  const [title, setTitle] = useState("");
  const [resource, setResource] = useState("");
  const [ob, setob] = useState({ name: "", title: "", resource: "" });
  const handleAddResource = () => {
    alert(`${title} ,${resource}`);
    setob({ ...ob, name: name, title: title, resource: resource });
    l.push(ob);
    console.log(l);
  };
  if (!isOpen) return null;
  return (
    <Dialog open={isOpen} onClose={onClose}>
      <DialogTitle>{"Add a Resouce"}</DialogTitle>
      <DialogContent>
        <DialogContentText>
          <div className="input-group mb-3">
            <label>
              Name
              <input
                type="text"
                className="form-control"
                placeholder="Enter your name here..."
                onChange={(event) => {
                  setName(event.target.value);
                }}
              />
            </label>
          </div>
          <div className="input-group mb-3">
            <label>
              Title
              <input
                type="text"
                className="form-control"
                onChange={(event) => {
                  setTitle(event.target.value);
                }}
              />
            </label>
          </div>
          <div className="input-group mb-3">
            <label>
              Resouce Link
              <input
                type="text"
                className="form-control"
                onChange={(event) => {
                  setResource(event.target.value);
                }}
              />
            </label>
          </div>
        </DialogContentText>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleAddResource}>Add</Button>
        <Button onClick={onClose} color="primary" autoFocus>
          Close
        </Button>
      </DialogActions>
    </Dialog>
  );
}
export default AddResource;
