import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import AddResource from "./AddResource";
import { BsInstagram, BsTwitter, BsFacebook, BsLinkedin } from "react-icons/bs";
import { IoIosAddCircle } from "react-icons/io";
function FronEndComponent({ n1, n2, topic }) {
  const [open, setOpen] = useState(false);

  const handleClickToOpen = () => {
    setOpen(true);
  };

  const handleToClose = () => {
    setOpen(false);
  };

  return (
    <div className="col-xl-6 mb-4">
      <div className="card">
        <div className="card-body">
          <div className="d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <button type="button" class="btn btn-secondary ">
                WEEK
                <br /> ({n1} - {n2})
              </button>
              <div className="ms-3">
                <h4 className="fw-bold mb-1">{topic}</h4>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="card-footer border-0 bg-light p-2 d-flex justify-content-around">
        <button
          type="button"
          class="btn btn-outline-secondary btn-sm"
          onClick={handleClickToOpen}
        >
          <IoIosAddCircle /> &nbsp; Add Resource
        </button>
        <AddResource isOpen={open} onClose={handleToClose} />
        <button type="button" class="btn btn-outline-secondary btn-sm  ">
          <NavLink
            to="/viewresource"
            role="button"
            style={{ textDecoration: "none", color: "secondary" }}
          >
            View Resources
          </NavLink>
        </button>
        {/* <a
          class="btn btn-link m-0 text-reset"
          href="#"
          role="button"
          data-ripple-color="primary"
          style={{ textDecoration: "none" }}
        >
          <BsInstagram /> &nbsp; Add Resource
          <i class="fas fa-envelope ms-2"></i>
        </a>
        <a
          class="btn btn-link m-0 text-reset"
          href="#"
          role="button"
          data-ripple-color="primary"
        >
          View Resources<i class="fas fa-phone ms-2"></i> 
           </a>*/}
      </div>
    </div>
  );
}

function FrontEnd() {
  return (
    <div>
      <h1 style={{ textAlign: "center", marginBottom: "20px" }}>FRONTEND</h1>
      <div className="row">
        <FronEndComponent
          n1={1}
          n2={2}
          topic="  React fundamentals and Hooks"
        />
        <FronEndComponent
          n1={3}
          n2={4}
          topic="  React Routing, Redux and Git"
        />
        <FronEndComponent n1={5} n2={6} topic=" React Testing" />
        <FronEndComponent n1={7} n2={8} topic="  Project" />
      </div>
    </div>
  );
}

export default FrontEnd;
